/**
earcut
**/
DROP FUNCTION IF EXISTS plv8.earcut(geom JSONB);
CREATE OR REPLACE FUNCTION plv8.earcut(geom JSONB)
RETURNS JSONB
immutable language plv8
as $$
var startT = new Date();
var data = earcut.flatten(geom.coordinates);
var result = earcut(data.vertices); 
var endT = new Date();
//plv8.elog(NOTICE, 'CalcTime: ' + (endT - startT) / 1000);
var triangles = [];
for (i = 0; i < result.length; i++) {
    var index = result[i];
    triangles.push([data.vertices[index * data.dimensions], data.vertices[index * data.dimensions + 1]]);
}
var returnval = {
  "type": "MultiPolygon",
  "coordinates": []
};
for (i = 0; triangles && i < triangles.length; i += 3) {
    let triangle = [triangles.slice(i, i + 3)];
    triangle[0].push(triangle[0][0]);
    returnval.coordinates.push(triangle);
}
return returnval;
$$;

